import sys
from PyQt5.QtWidgets import QApplication, QMainWindow, QPushButton
from PyQt5.QtGui import QIcon


# 假设你已经有了一个二进制的图片数据
# binary_data = ...

def create_icon_from_binary(binary_data):
    # 创建一个临时文件并写入二进制数据
    import tempfile
    with tempfile.NamedTemporaryFile(suffix='.png', delete=False) as f:
        f.write(binary_data)
        f.flush()  # 确保写入磁盘
        icon = QIcon(f.name)
    return icon


